<?php	session_start();	if(!isset($_SESSION['login']) || $_SESSION['login']!=1) echo "Kein Zugang! <a href=\"login.php\">Erstmal einloggen!</a>";	else{		include "./config.php";		echo"<html><head><link rel=\"stylesheet\" type=\"text/css\" href=\"style.css\"></head><body>\n";		echo "<div id=\"nav\">\n";		include "nav.php";		echo "</div>\n";		echo "<div id=\"mainframe\">";		echo "<table class=\"artl\" border=1>\n<tr>\n";		echo "<th>ID</th>			  <th>Date</th>			  <th>Title</th>			  <th>Content</th>			  <th>Categories</th>			  <th>Bearbeiten</th>";		$query=mysql_query("SELECT * FROM ".$SITEVARS['DB_PREFIX']."content ORDER BY id DESC");		while ($datensatz=mysql_fetch_array($query)){			$content = (strlen($datensatz['content'])>30) ? (substr($datensatz['content'],0,30)."...") : $datensatz['content'];			echo "<tr><td>".$datensatz['id']."</td>				  <td>".$datensatz['date']."</td>				  <td>".$datensatz['title']."</td>				  <td>".$content."</td>				  <td>".substr($datensatz['categories'],2)."</td>				  <td><a href=\"edit_content.php?PHPSESSID=".session_id()."&id=".$datensatz['id']."\">Inhalt bearbeiten</a><br>						DUMMY</td></tr>";		}		echo "\n</table>\n";		echo "</div>\n";		echo"</body></html>";	}?>